    let el = marker;
    for (let i = 0; i < 5; i++) {
        if (el.tagName === 'P' || el.tagName === 'LI') break;
        el = el.parentElement;
    }
    results.push(el ? el.innerText.substring(0, 800) : 'not found');
}
