// Get the full details of reference cite_note-3 (Bachelier 1900)
const ref3 = document.getElementById('cite_note-3');
if (ref3) {
    // Get all links in this reference
    const links = ref3.querySelectorAll('a');
    const linkDetails = [...links].map(a => ({href: a.href, text: a.innerText}));
    return {
        fullText: ref3.innerText,
        links: linkDetails
    };
}

const ref3 = document.getElementById('cite_note-3');
const links = ref3 ? [...ref3.querySelectorAll('a')].map(a => ({href: a.href, text: a.innerText})) : [];
JSON.stringify({fullText: ref3 ? ref3.innerText : 'not found', links: links});

// Find all citation markers in the article that link to cite_note-3
const markers = document.querySelectorAll('a[href="#cite_note-3"]');
const markerDetails = [...markers].map(a => {
    // Get surrounding text
    const parent = a.parentElement;
    return {
        linkText: a.innerText,
        parentText: parent ? parent.innerText.substring(0, 500) : 'N/A'
    };
});
JSON.stringify(markerDetails);

// Find all citation markers in the article that link to cite_note-3 - get surrounding sentence
const markers = document.querySelectorAll('a[href="#cite_note-3"]');
const markerDetails = [...markers].map(a => {
    // Go up multiple levels to get surrounding sentence
    let el = a;
    for (let i = 0; i < 5; i++) {
        if (el.parentElement) el = el.parentElement;
    }
    return {
        linkText: a.innerText,
        surroundingText: el.innerText.substring(0, 600)
    };
});
JSON.stringify(markerDetails);

// Find the superscript that contains the cite_note-3 link
const markers = document.querySelectorAll('sup a[href="#cite_note-3"]');
const details = [...markers].map(a => {
    const sup = a.parentElement; // the sup element
    const container = sup.parentElement; // the containing paragraph or element
    return {
        linkText: a.innerText,
        containerTag: container ? container.tagName : 'N/A',
        containerText: container ? container.innerText.substring(0, 600) : 'N/A'
    };
});
JSON.stringify(details);

// Get the full reference text for cite_note-3
const ref3 = document.getElementById('cite_note-3');
JSON.stringify({
    fullText: ref3 ? ref3.innerText : 'not found',
    links: ref3 ? [...ref3.querySelectorAll('a')].map(a => ({href: a.href, text: a.innerText.trim()})) : []
});

// Get surrounding sentence for cite_note-3
const marker = document.querySelector('sup a[href="#cite_note-3"]');
const parentSup = marker ? marker.parentElement : null;
const parentP = parentSup ? parentSup.parentElement : null;
JSON.stringify({
    markerText: marker ? marker.innerText : 'N/A',
    paragraph: parentP ? parentP.innerText.substring(0, 800) : 'N/A'
});
