// Let me search for the earliest years in ALL references
const refList = [...document.querySelectorAll('.references li')];
const yearPattern = /\b(1[89]\d{2}|200\d|201\d)\b/g;
const results = [];

for (let li of refList) {
    const text = li.innerText.substring(0, 400);
    const years = text.match(yearPattern);
    if (years) {
        const minYear = Math.min(...years.map(y => parseInt(y)));
        if (minYear <= 2000) {  // Focus on old ones
            results.push({ id: li.id, minYear, text: text.substring(0, 250) });
        }
    }
}
results.sort((a, b) => a.minYear - b.minYear);
JSON.stringify(results.slice(0, 20));

// Search for actual publication year patterns - looking for years in the context of dates
// Pattern: author (YEAR), or year after a date pattern
const refList = [...document.querySelectorAll('.references li')];
const results = [];

for (let li of refList) {
    const text = li.innerText.substring(0, 500);
    // Match author/date patterns like "(Month YEAR)" or "(YEAR)"
    const datePatterns = text.match(/\(\w+ \d{4}\)|\(\w+ \d{1,2}, \d{4}\)|\(\d{4}\)/g);
    if (datePatterns) {
        const years = datePatterns.map(p => p.match(/\d{4}/)).filter(m => m).map(m => parseInt(m[0]));
        const minYear = Math.min(...years);
        if (minYear <= 2000) {
            results.push({ id: li.id, minYear, text: text.substring(0, 300) });
        }
    }
    // Also check for journal volume/issue format "YYYY. volume"
    const altDate = text.match(/\. (\d{4})\./);
    if (altDate) {
        const yr = parseInt(altDate[1]);
        if (yr <= 2000) {
            results.push({ id: li.id, minYear: yr, text: text.substring(0, 300), note: 'journal date' });
        }
    }
}
results.sort((a, b) => a.minYear - b.minYear);
JSON.stringify(results.slice(0, 20));

// Let me also check for "YEAR." pattern (year followed by period typical of journal references)  
const refList = [...document.querySelectorAll('.references li')];
const results = [];

for (let li of refList) {
    const text = li.innerText.substring(0, 600);
    // Look for years between 1980-2009 in various positions
    const allYears = [...text.matchAll(/\b(1[89]\d{2}|200\d)\b/g)].map(m => parseInt(m[1]));
    if (allYears.length > 0) {
        const minYear = Math.min(...allYears);
        if (minYear <= 2005) {
            results.push({ id: li.id, minYear, text: text.substring(0, 350) });
        }
    }
}
results.sort((a, b) => a.minYear - b.minYear);
// Deduplicate by id
const seen = new Set();
const unique = results.filter(r => {
    if (seen.has(r.id)) return false;
    seen.add(r.id);
    return true;
});
JSON.stringify(unique.slice(0, 30));

