// Check the bibliography section
const bibl = document.querySelectorAll('.reflist ol li, .bibliography li');
[...bibl].slice(0, 30).map(li => ({ id: li.id, text: li.innerText.substring(0, 300) }));

// Look for bibliography entries (the short-form references like "Metropolis 1987")
const allText = document.body.innerText;
// Find bibliography section
const biblStart = allText.indexOf('Bibliography');
const biblEnd = allText.indexOf('External links');
allText.substring(biblStart, biblEnd).substring(0, 3000);

// Find the bibliography / works cited section with Harvard-style refs
const h3s = [...document.querySelectorAll('h2, h3')].map(h => h.innerText);
h3s;

// Find the Sources section
const sourcesHeading = [...document.querySelectorAll('h3')].find(h => h.innerText === 'Sources');
if (sourcesHeading) {
    let el = sourcesHeading.nextElementSibling;
    let text = '';
    while (el && el.tagName !== 'H2' && el.tagName !== 'H3') {
        text += el.innerText + '\n';
        el = el.nextElementSibling;
    }
    text.substring(0, 5000);
} else {
    'not found';
}

// Try to find sources/bibliography entries
const sourcesHeading = [...document.querySelectorAll('h3')].find(h => h.innerText.trim() === 'Sources');
if (sourcesHeading) {
    let el = sourcesHeading.parentElement;
    let text = el ? el.outerHTML.substring(0, 200) : 'no parent';
    text;
}

// Find the Sources section div and get all content after it
const sourcesDiv = [...document.querySelectorAll('.mw-heading')].find(h => h.innerText.trim().startsWith('Sources'));
if (sourcesDiv) {
    let el = sourcesDiv.nextElementSibling;
    let text = '';
    let count = 0;
    while (el && count < 10) {
        text += el.innerText + '\n---\n';
        el = el.nextElementSibling;
        count++;
    }
    text.substring(0, 5000);
}

// Get more of the Sources section
const sourcesDiv = [...document.querySelectorAll('.mw-heading')].find(h => h.innerText.trim().startsWith('Sources'));
if (sourcesDiv) {
    let el = sourcesDiv.nextElementSibling;
    let text = '';
    let count = 0;
    while (el && count < 10) {
        text += el.innerText + '\n---\n';
        el = el.nextElementSibling;
        count++;
    }
    text.substring(5000, 10000);
}

